                variant="outlined"
                size="small"
              />
            </Grid>

            {/* age */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <TextValidator
                className="w-100 mb-16"
                label={
                  <span className="font">
                    <span style={{ color: "red" }}> * </span>
                    {("user.age")}
                  </span>
                }
                type="number"
                name="age"
                value={age ? age : ""}
                onChange={(e) => setAge(e.target.value)}
                validators={["required", "isAge"]}
                errorMessages={[
                  ("general.errorMessages_required"),
