              <TextValidator
                className="w-100 mb-16"
                label={
                  <span className="font">
                    <span style={{ color: "red" }}> * </span>
                    {t("Email")}
                  </span>
                }
                type="email"
                name="email"
                value={email ? email : ""}
                onChange={(e) => setEmail(e.target.value)}
                validators={["required", "isEmail"]}
                errorMessages={[
                  ("general.errorMessages_required"),
                  ("general.errorMessages_email_valid"),
                ]}
                variant="outlined"
                size="small"
              />
            </Grid>

            {/* code */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <TextValidator
                className="w-100 mb-16"
                label={
                  <span className="font">
                    <span style={{ color: "red" }}> * </span>
                    {("user.code")}
                  </span>
                }
                type="text"
                name="code"
                value={code ? code : ""}
                onChange={(e) => setCode(e.target.value)}
                validators={["required", "isCode"]}
                errorMessages={[
                  ("general.errorMessages_required"),
                  "Code phải đủ 6-10 ký tự",
                ]}
                variant="outlined"
                size="small"
              />
            </Grid>

            {/* phone */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <TextValidator
                className="w-100 mb-16"
                label={
                  <span className="font">
                    <span style={{ color: "red" }}> * </span>
                    {("user.phone")}
                  </span>
                }
                type="tel"
                name="phone"
                value={phone ? phone : ""}
                onChange={(e) => setPhone(e.target.value)}
                validators={["required", "isPhone"]}
                errorMessages={[
                  ("general.errorMessages_required"),
                  "Nhập đúng định dạng sđt",
                ]}
                variant="outlined"
                size="small"
              />
            </Grid>

            {/* province
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <FormControl
                className="mb-16"
                fullWidth={true}
                variant="outlined"
                size="small"
              >
                <InputLabel htmlFor="province-simple">
                  {<span className="font">{t("user.province")}</span>}
                </InputLabel>
                <Select
                  value={
                    JSON.stringify(address.province) === JSON.stringify({})
                      ? null
                      : address.province
                  }
                  onChange={(e) => (handleClose)}
                  inputProps={{
                    name: "province",
                    id: "province-simple",
                  }}
                >
                  {listProvince.map((province) => {
                    return (
                      <MenuItem key={province.id} value={province}>
                        {province.name}
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Grid> */}

