            {/* <Grid item lg={4} md={4} sm={4} xs={12}>
              <FormControl fullWidth={true} variant="outlined" size="small">
                <InputLabel htmlFor="commune-simple">
                  {<span className="font">{t("user.commune")}</span>}
                </InputLabel>
                <Select
                  value={
                    JSON.stringify(address.commune) === JSON.stringify({})
                      ? null
                      : address.commune
                  }
                  onChange={(e) => handleClose}
                  onOpen={handleClose}
                  inputProps={{
                    name: "commune",
                    id: "commune-simple",
                  }}
                >
                  {address.communesByDistrict.map((commune) => {
                    return (
                      <MenuItem key={commune.id} value={commune}>
                        {commune.name}
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Grid> */}
          </Grid>
        </DialogContent>

        <DialogActions spacing={4} className="flex flex-end flex-middle">
          <Button
            variant="contained"
            color="secondary"
            onClick={() => handleClose()}
          >
            {t("Cancel")}
          </Button>
          <Button
            variant="contained"
            color="primary"
            type="submit"
            disabled={loading}
          >
            {t("Save")}
          </Button>
        </DialogActions>
      </ValidatorForm>
    </Dialog>
  );
}

