import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import { useState } from "react";
import { useTranslation } from "react-i18next";
import i18n from "i18n";
import { Grid, InputLabel, FormControl } from "@material-ui/core";
export default function FormDialog(props) {
  const { handleClose, item } = props;
  const [open, setOpen] = React.useState(true);

  const [name, setName] = useState(item?.name);
  const [age, setAge] = useState(item?.age);
  const [email, setEmail] = useState(item?.email);
  const [code, setCode] = useState(item?.code);
  const [phone, setPhone] = useState(item?.phone);
  const { t } = useTranslation("translation");
  // const handleClickOpen = () => {
  //   setOpen(true);
  // };

  // const handleClose = () => {
  //   setOpen(false);
  // };

  return (
    <div>
      {/* <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open form dialog
      </Button> */}

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here.
            We will send updates occasionally.
          </DialogContentText>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Họ Và Tên"
              type="email"
              fullWidth
              value={name ? name : ""}
              onChange={(e) => setName(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Tuổi"
              type="email"
              fullWidth
              value={age ? age : ""}
              onChange={(e) => setAge(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Email"
              type="email"
              fullWidth
              value={email ? email : ""}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Code"
              type="email"
              fullWidth
              value={code ? code : ""}
              onChange={(e) => setCode(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Phone"
              type="email"
              fullWidth
              value={phone ? phone : ""}
              onChange={(e) => setPhone(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <FormControl
              className="mb-16"
              fullWidth={true}
              variant="outlined"
              size="small"
            >
              <InputLabel htmlFor="province-simple">
                {<span className="font">{t("user.province")}</span>}
              </InputLabel>
              <Select
                value={
                  JSON.stringify(address.province) === JSON.stringify({})
                    ? null
                    : address.province
                }
                onChange={(e) => handleChange(e)}
                inputProps={{
                  name: "province",
                  id: "province-simple",
                }}
              >
                {listProvince.map((province) => {
                  return (
                    <MenuItem key={province.id} value={province}>
                      {province.name}
                    </MenuItem>
                  );
                })}
              </Select>
            </FormControl>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
