import React from "react";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import { useState } from "react";
import { useEffect } from "react";
import { useTranslation } from "react-i18next";
import i18n from "i18n";
import {
  Grid,
  InputLabel,
  FormControl,
  MenuItem,
  Select,
} from "@material-ui/core";
export default function FormDialog(props) {
  const { handleClose, item } = props;
  const [open, setOpen] = React.useState(true);
  const [listProvince, setListProvince] = useState([]);
  const [listDistrict, setListDistrict] = useState([]);
  const [listCommune, setListCommune] = useState([]);

  const [name, setName] = useState(item?.name);
  const [age, setAge] = useState(item?.age);
  const [email, setEmail] = useState(item?.email);
  const [code, setCode] = useState(item?.code);
  const [phone, setPhone] = useState(item?.phone);

  const [address, setAddress] = useState({
    province: {},
    district: {},
    commune: {},
    districtsByProvince: [],
    communesByDistrict: [],
  });
  const handleChange = (e) => {
    if (e.target.name === "province") {
      setAddress({
        ...address,
        province: e.target.value,
        district: {},
        commune: {},
        districtsByProvince: [],
        communesByDistrict: [],
      });
    }

    if (e.target.name === "district") {
      const list = listCommune.filter(
        (commune) => commune.district.name == e.target.value.name
      );
      setAddress({
        ...address,
        district: e.target.value,
        commune: {},
        communesByDistrict: list,
      });
    }

    if (e.target.name === "commune") {
      setAddress({
        ...address,
        commune: e.target.value,
      });
    }
  };

  const { t } = useTranslation("translation");
  // const handleClickOpen = () => {
  //   setOpen(true);
  // };

  // const handleClose = () => {
  //   setOpen(false);
  // };
  
  // const handleOpenDistrict = () => {
  //   const checkEmptyProvince =
  //     Object.keys(address.province).length === 0 &&
  //     address.province.constructor === Object;

  //   if (checkEmptyProvince) {
  //     return;
  //   } else {
  //     setAddress({
  //       ...address,
  //       districtsByProvince: listDistrict,
  //     });
  //   }
  // };

  // const handleOpenCommune = () => {
  //   const checkEmptyDistrict =
  //     Object.keys(address.district).length === 0 &&
  //     address.district.constructor === Object;
  //   if (checkEmptyDistrict) {
  //     return;
  //   }
  // };
  useEffect(() => {
    async function fetchAPIProvinces() {
      const response = await fetch(
        "http://training-api.oceantech.com.vn/cms/provinces/search",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: "" }),
        }
      );
      const responseJSON = await response.json();
      const listProvince = responseJSON.data;
      console.log(listProvince);
      // const data2 = listProvince.map((record) => {
      //   return {
      //     id: record.id,
      //     name: record.name,
      //   };
      // });
      // console.log(data2);
      setListProvince(listProvince);
    }
    fetchAPIProvinces();
  }, []);
  useEffect(() => {
    async function fetchAPIProvinces() {
      const response = await fetch(
        "http://training-api.oceantech.com.vn/cms/provinces/search",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: "" }),
        }
      );
      const responseJSON = await response.json();
      const listProvince = responseJSON.data;
      console.log(listProvince);
      // const data2 = listProvince.map((record) => {
      //   return {
      //     id: record.id,
      //     name: record.name,
      //   };
      // });
      // console.log(data2);
      setListProvince(listProvince);
    }
    fetchAPIProvinces();
  }, []);
  useEffect(() => {
    async function fetchAPICommune() {
      const response = await fetch(
        "http://training-api.oceantech.com.vn/cms/communes/search",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: "" }),
        }
      );
      const responseJSON = await response.json();
      const listCommune = responseJSON;
      console.log(listProvince);
      // const data2 = data.map((record) => {
      //   return {
      //     name: record.name,
      //   };
      // });
      // console.log(data2);
      setListCommune(listCommune);
    }
    fetchAPICommune();
  }, []);

  return (
    <div>
      {/* <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open form dialog
      </Button> */}

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {/* {[listProvince].map((province) => {
              console.log(province.data);
              return (
                123
              )
              
            })} */}
          </DialogContentText>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Họ Và Tên"
              type="email"
              fullWidth
              value={name ? name : ""}
              onChange={(e) => setName(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Tuổi"
              type="email"
              fullWidth
              value={age ? age : ""}
              onChange={(e) => setAge(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Email"
              type="email"
              fullWidth
              value={email ? email : ""}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Code"
              type="email"
              fullWidth
              value={code ? code : ""}
              onChange={(e) => setCode(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Phone"
              type="email"
              fullWidth
              value={phone ? phone : ""}
              onChange={(e) => setPhone(e.target.value)}
            />
          </Grid>
          <Grid item lg={4} md={4} sm={4} xs={12}>
            <FormControl
              className="mb-16"
              fullWidth={true}
              variant="outlined"
              size="small"
            >
              <InputLabel htmlFor="province-simple">
                {<span className="font">{t("user.province")}</span>}
              </InputLabel>
              <Select
                value={
                  JSON.stringify(address.province) === JSON.stringify({})
                    ? null
                    : address.province
                }
                onChange={(e) => handleChange(e)}
                inputProps={{
                  name: "province",
                  id: "province-simple",
                }}
              >
                {listProvince.map((province) => {
                  return (
                    <MenuItem key={province.id} value={province}>
                      {province.name}
                    </MenuItem>
                  );
                })}
              </Select>
            </FormControl>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
