import React from "react";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import { useTheme } from "@material-ui/core/styles";
import { useState } from "react";
import {
  Grid,
  InputLabel,
  FormControl,
  MenuItem,
  Select,
} from "@material-ui/core";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
export default function ResponsiveDialog(props) {
  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const { t, item } = props;
  const [name, setName] = useState(item?.name);
  const [age, setAge] = useState(item?.age);
  const [email, setEmail] = useState(item?.email);
  const [code, setCode] = useState(item?.code);
  const [phone, setPhone] = useState(item?.phone);
  return (
    <Dialog
      open={open}
      PaperComponent={PaperComponent}
      maxWidth={"md"}
      fullWidth={true}
    >
      <div className={clsx("wrapperButton", !loading && "hidden")}>
        <CircularProgress className="buttonProgress" size={24} />
      </div>

      <DialogTitle style={{ cursor: "move" }} id="draggable-dialog-title">
        <span className="mb-20 styleColor">
          {" "}
          {(id ? t("general.update") : t("Add")) +
            " " +
            t("user.title_Employees")}{" "}
        </span>

        <IconButton
          style={{ position: "absolute", right: "10px", top: "10px" }}
          onClick={() => handleClose()}
        >
          <Icon color="error" title={t("close")}>
            close
          </Icon>
        </IconButton>
      </DialogTitle>

      <ValidatorForm
        // ref="form"
        onSubmit={handleSave}
        style={{
          overflowY: "auto",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <DialogContent dividers>
          <Grid className="mb-16" container spacing={1}>
            {/* name */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <TextValidator
                className="w-100 mb-16"
                label={
                  <span className="font">
                    <span style={{ color: "red" }}> * </span>
                    {t("user.displayName")}
                  </span>
                }
                type="text"
                name="name"
                value={name ? name : ""}
                onChange={(e) => setName(e.target.value)}
                validators={["required", "isName"]}
                errorMessages={[
                  t("general.errorMessages_required"),
                  "Họ và tên ít nhất 6 ký tự",
                ]}
                variant="outlined"
                size="small"
              />
            </Grid>

           
            
            {/* district */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <FormControl
                className="mb-16"
                fullWidth={true}
                variant="outlined"
                size="small"
              >
                <InputLabel htmlFor="district-simple">
                  {<span className="font">{t("user.district")}</span>}
                </InputLabel>
                <Select
                  value={
                    JSON.stringify(address.district) === JSON.stringify({})
                      ? null
                      : address.district
                  }
                  onChange={(e) => handleChange(e)}
                  onOpen={handleOpenDistrict}
                  inputProps={{
                    name: "district",
                    id: "district-simple",
                  }}
                >
                  {address.districtsByProvince.map((district) => {
                    return (
                      <MenuItem key={district.id} value={district}>
                        {district.name}
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Grid>

            {/* commune */}
            <Grid item lg={4} md={4} sm={4} xs={12}>
              <FormControl fullWidth={true} variant="outlined" size="small">
                <InputLabel htmlFor="commune-simple">
                  {<span className="font">{t("user.commune")}</span>}
                </InputLabel>
                <Select
                  value={
                    JSON.stringify(address.commune) === JSON.stringify({})
                      ? null
                      : address.commune
                  }
                  onChange={(e) => handleChange(e)}
                  onOpen={handleOpenCommune}
                  inputProps={{
                    name: "commune",
                    id: "commune-simple",
                  }}
                >
                  {address.communesByDistrict.map((commune) => {
                    return (
                      <MenuItem key={commune.id} value={commune}>
                        {commune.name}
                      </MenuItem>
                    );
                  })}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </DialogContent>

        <DialogActions spacing={4} className="flex flex-end flex-middle">
          <Button
            variant="contained"
            color="secondary"
            onClick={() => handleClose()}
          >
            {t("Cancel")}
          </Button>
          <Button
            variant="contained"
            color="primary"
            type="submit"
            disabled={loading}
          >
            {t("Save")}
          </Button>
        </DialogActions>
      </ValidatorForm>
    </Dialog>
  );
}
